import "bootstrap/dist/css/bootstrap.min.css";

import Imagen from "./components/Imagen";
import Titulo from "./components/Titulo";
import Parrafo from "./components/Parrafo";
import Enlace from "./components/Enlace";

function ComponentesHTML() {
  const mostrarAlerta = () => {
    alert("Componente interno activado");
  };

  return (
    <div className="p-4 border rounded bg-light">
      <Titulo texto="Componente Interno" nivel={2} />
      <Parrafo texto="Este componente contiene lógica propia, como un botón." />
      <button className="btn btn-warning" onClick={mostrarAlerta}>
        Mostrar Alerta
      </button>
    </div>
  );
}

function App() {
  return (
    <div className="container mt-4">
      <ComponentesHTML />

      <Titulo texto="Bienvenido a la app" nivel={1} />
      <Parrafo texto="Este párrafo es generado por un componente funcional externo." />
      <Imagen
        src="https://via.placeholder.com/200"
        alt="Imagen de prueba"
        width="200"
        height="200"
      />
      <br />
      <Enlace href="https://reactjs.org" texto="Documentación oficial de React" />
    </div>
  );
}

export default App;
